<script lang="ts">
	import { states } from './states.svelte';

	type Props = {
		onclose?: () => void;
		onchange?: (mode: string) => void;
	};
	let { onclose, onchange }: Props = $props();

	function close() {
		onclose?.();
	}

	function onChange(mode: string) {
		return () => {
			states.menu = mode;
			close();
			onchange?.(mode);
		};
	}
</script>

